package week1.day3;

public class Swap {

	public Swap() {  
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
	
		
		a=a+b;
		b=a-b;
	
		

		System.out.println("b="+b);
		a=a-b;
		System.out.println("a="+a);
		
	

	}

}
